export const COURSES = RAW_COURSES.map(c => ({
  ...c,
  slug: _slug(c.name),
  holes: c.holes || "18",
  state: "NE",
  address_full: c.address_full || [c.street, c.city, "NE", c.zip].filter(Boolean).join(", "),
  featured: c.featured || false,
}));

